$(document).ready(function () {

    $('.fake-select').each(function() {
        const v = $(this).find('select option:selected').text();
        const fsi = "<div class='fake-select-input'><div class='fake-select-value'>"+v+"</div></div>";
        const fsb = "<div class='fake-select-body'>";
        $(this).append(fsi).append(fsb);
        $(this).find('select').children('option:not(:first)').each(function() {
            const val = $(this).val(), text = $(this).text();
            if($(this).is(':selected')) {
                $(this).closest(".fake-select").find('.fake-select-body').append('<div class="fake-select-option fake-option-selected" data-value="'+val+'" role="option" title="'+text+'">'+text+'</div>');
            } else {
                $(this).closest(".fake-select").find('.fake-select-body').append('<div class="fake-select-option" data-value="'+val+'" role="option" title="'+text+'">'+text+'</div>');
            }
        });
    });

    $(".fake-select-input").on("click", function() {
        if ($(this).is(".fake-select-open")) {
            $(".fake-select").removeClass("fake-open");
            $(this).removeClass("fake-select-open");
        } else {
            $(".fake-select-input").removeClass("fake-select-open");
            $(this).addClass("fake-select-open");
            $(".fake-select").removeClass("fake-open");
            $(this).parent(".fake-select").addClass("fake-open");
        }
    });
    $(".fake-select-option").on("click", function() {
        $(this).closest("option:selected").removeAttr("selected");
        $(this).closest(".fake-select").find("option:selected").removeAttr("selected");
        if ($(this).is(".fake-option-selected")) {
            $(this).removeClass("fake-option-selected");
            const v = $(this).closest(".fake-select").find('select option:first').text();
            $(this).closest(".fake-select").find(".fake-select-value").text(v);
        } else {
            $(this).siblings().removeClass("fake-option-selected");
            const b = $(this).attr("title"), a = $(this).data("value");

            console.log(a);
            $(this).addClass("fake-option-selected");
            $(this).closest(".fake-select").find(".fake-select-input").addClass("fake-selected");
            $(this).closest(".fake-select").find(".fake-select-value").text(b);
            $(this).closest(".fake-select").find("option[value='"+ a +"']").attr("selected", "selected");
        }
        $(".fake-select-input").removeClass("fake-select-open");
        $(".fake-select").removeClass("fake-open");
    });

// Ruchoma galeria
    $('.col-gallery-thumb').each( function() { $(this).hoverdir(); } );

// Lightbox
    $( '.swipebox' ).swipebox({useSVG : false});

// Slider
    $( '.textSlider ul' ).responsiveSlides({auto:true, pager:false, nav:true, timeout:5000, random:false, speed: 500});

// Youtube
    $('iframe[src*="youtube"]').wrap("<div class='video-container'></div>");

    let elmSelect = document.getElementById('filtr-sort');

    if (!!elmSelect) {
        elmSelect.addEventListener('change', e => {
            let choice = e.target.value;
            if (!choice) return;

            let url = new URL(window.location.href);
            url.searchParams.set('sort', choice);
            window.location.href = url;
        });
    }

    $("#triggermenu, .closemenu").click(function(b){
        b.preventDefault();
        $('body').toggleClass('openmenu');
    });
    $("#megamenu-opacity").click(function (event) {
        if ($('body').hasClass("openmenu")) {
            $('body').removeClass("openmenu");
        }
        event.preventDefault();
    });

    var headerHeight = $('#header').outerHeight();
    $(".scroll-to").click(function (e) {
        e.preventDefault();
        const section = $(this).attr('href');
        const offset = $(this).data("offset");
        $('html, body').stop().animate({
            scrollTop: $(section).offset().top - headerHeight - offset
        }, 1500, 'easeInOutExpo');
    });

});
document.addEventListener("DOMContentLoaded", function() {
    const header = $('#header');
    const aboveHeight = 20;

    function onScroll() {
        if ($(window).scrollTop() > aboveHeight && !header.hasClass('fixed')) {
            header.addClass('fixed');
            header.css({
                animation: "stickymenu 500ms",
                animationFillMode: "forwards"
            });
        }
        if ($(window).scrollTop() < aboveHeight && header.hasClass('fixed')) {
            header.removeClass('fixed');
            header.removeAttr('style');
        }
    }
    window.addEventListener('scroll', function() {
        requestAnimationFrame(onScroll);
    });

    onScroll();
});
